CREATE DATABASE Travel;
-- drop database Travel;
-- show tables;
 USE Travel;

CREATE TABLE `user` -- 用户表（加上俩点是为了跟关键字区别开来）
(
	userid CHAR(32) PRIMARY KEY,-- 用户编号
	username CHAR(50), -- 用户名：用邮箱,用邮箱登录
	`password` CHAR(32),-- 密码
	nickname CHAR(32), -- 昵称
	image VARCHAR(200)-- 头像
);
-- select * from user;
-- drop table user;
-- delete from user where userid>1;

INSERT INTO `user` VALUES(1,'1234@qq.com','123','zhangsan','images/chenkun.jpg');
-- select * from user;
CREATE TABLE city-- 城市
(
	cityid CHAR(32) PRIMARY KEY,-- 城市id
	`name` VARCHAR(100), -- 城市名
	introduce VARCHAR(500), -- 介绍	
	image VARCHAR(200) -- 城市图片	
);
-- drop table city;
-- SELECT * FROM city;
UPDATE `city` SET introduce='简称巴和渝，别称巴渝、山城、渝都、桥都，雾都，是中华人民共和国中央直辖市、国家中心城市、超大城市、世界温泉之都、长江上游地区经济中心、金融中心和创新中心，政治、文化、科技、教育、艺术等中心，国务院定位的国际大都市。';
INSERT INTO `city` VALUES('1','重庆','美食的天堂——重庆！','images/city/aidingbao.png');
INSERT INTO `city` VALUES('2','北京','美食的天堂——北京！','images/city/aidingbao.png');
INSERT INTO `city` VALUES('3','巴黎','美食的天堂——巴黎！','images/city/aidingbao.png');
INSERT INTO `city` VALUES('4','爱丁堡','欧洲风情——爱丁堡！','images/city/aidingbao.png');
INSERT INTO `city` VALUES('5','云南','大自然的馈赠——云南！','images/city/aidingbao.png');
CREATE TABLE cityStrategy -- 城市攻略 注意：把城市攻略保存成一个html文件，用户浏览时直接把这个文件内嵌在网页中
(
	cstid CHAR(32) PRIMARY KEY, -- id
	simage VARCHAR(200), -- 小封面图片
	title VARCHAR(100),-- 标题
	`time` DATETIME,-- 发表时间
	pageview INT,-- 浏览量
	content MEDIUMTEXT, -- 保存成html代码的攻略内容
	cityid CHAR(32),-- 所属城市的id
	userid CHAR(32), -- 作者的用户id
	FOREIGN KEY (cityid) REFERENCES city(cityid), -- 外键	
	FOREIGN KEY (userid) REFERENCES `user`(userid) -- 外键	
);
-- drop table cityStrategy;
-- delete from cityStrategy;
-- SELECT * FROM cityStrategy;
-- update cityStrategy set title='夜',content="<p>1</p>" where cstid='ECC3ECFC26AF4F3EAEA54CD0204ADEE2';
INSERT INTO cityStrategy VALUES(1,'images/strategy/strategy01.jpg','深秋的云南','2016-01-03',50,
'
<div id="_j_photo_87101641" data-pid="87101641" class="add_pic _j_anchorcnt" style="margin: 0px 0px 18px; padding: 0px; word-break: break-all; word-wrap: break-word; position: relative; color: rgb(102, 102, 102); font-family: Arial, &#39;Lucida Grande&#39;, &#39;Microsoft Yahei&#39;, &#39;Hiragino Sans GB&#39;, &#39;Hiragino Sans GB W3&#39;, SimSun, STHeiti; font-size: 14px; line-height: 25px; white-space: normal; width: 680px;"><img src="http://localhost:8080/travel/ueditor/jsp/upload/20160109/77461452305337963.jpeg" _src="http://localhost:8080/travel/ueditor/jsp/upload/20160109/77461452305337963.jpeg"/></div><p class="_j_note_content" style="margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; line-height: 25px; font-size: 14px; color: rgb(102, 102, 102); font-family: Arial, &#39;Lucida Grande&#39;, &#39;Microsoft Yahei&#39;, &#39;Hiragino Sans GB&#39;, &#39;Hiragino Sans GB W3&#39;, SimSun, STHeiti; white-space: normal;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;人生的遭遇，大半是片段的欢乐换来终身的不安；<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;摄影，却是片刻的不欢带来终身的快乐。<br/><br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;——余光中《谁能叫世界停止三秒钟》</p><div id="_j_paragraph_1" data-index="1" class="article_title _j_anchorcnt" style="margin: 20px 0px; padding: 0px; word-break: break-all; word-wrap: break-word; position: relative; color: rgb(102, 102, 102); font-family: Arial, &#39;Lucida Grande&#39;, &#39;Microsoft Yahei&#39;, &#39;Hiragino Sans GB&#39;, &#39;Hiragino Sans GB W3&#39;, SimSun, STHeiti; font-size: 14px; line-height: 25px; white-space: normal;"><h2 class="t1" style="margin-right: 0px; margin-bottom: 0px; margin-left: 0px; padding: 0px; font-size: 22px; height: 86px; overflow: hidden; color: rgb(68, 68, 68); background: url(http://css.mafengwo.net/images/post/new_notes/nn_v2/t1.gif) 0px 0px no-repeat;"><span class="_j_anchor" style="line-height: 95px; display: block;">預告篇</span></h2></div><div id="_j_photo_86862636" data-pid="86862636" class="add_pic _j_anchorcnt" style="margin: 0px 0px 18px; padding: 0px; word-break: break-all; word-wrap: break-word; position: relative; color: rgb(102, 102, 102); font-family: Arial, &#39;Lucida Grande&#39;, &#39;Microsoft Yahei&#39;, &#39;Hiragino Sans GB&#39;, &#39;Hiragino Sans GB W3&#39;, SimSun, STHeiti; font-size: 14px; line-height: 25px; white-space: normal; width: 680px;"><img src="http://localhost:8080/travel/ueditor/jsp/upload/20160109/55691452305343798.jpeg" _src="http://localhost:8080/travel/ueditor/jsp/upload/20160109/55691452305343798.jpeg"/><span class="pic_tag" style="margin: 8px 0px 18px; line-height: 24px; display: block; color: rgb(153, 153, 153); font-size: 12px; width: 600px; padding-left: 0px;"><a href="http://www.mafengwo.cn/poi/2781.html" class="_j_anchor" data-poiid="2781" target="_blank" data-cs-p="ginfo_img_poi_new" style="color: rgb(153, 153, 153); cursor: pointer; outline: 0px; display: inline-block; position: relative;"><em class="house" style="width: 24px; height: 24px; margin-right: 10px; display: inline-block; vertical-align: -6px; background: url(http://css.mafengwo.net/images/post/new_notes/nn_v2/nn_sprite_v17.png) 0px -203px no-repeat;"></em>固东镇银杏村</a></span></div><div id="_j_photo_86894739" data-pid="86894739" class="add_pic _j_anchorcnt" style="margin: 0px 0px 18px; padding: 0px; word-break: break-all; word-wrap: break-word; position: relative; color: rgb(102, 102, 102); font-family: Arial, &#39;Lucida Grande&#39;, &#39;Microsoft Yahei&#39;, &#39;Hiragino Sans GB&#39;, &#39;Hiragino Sans GB W3&#39;, SimSun, STHeiti; font-size: 14px; line-height: 25px; white-space: normal; width: 680px;"><img src="http://localhost:8080/travel/ueditor/jsp/upload/20160109/56231452305352887.jpeg" _src="http://localhost:8080/travel/ueditor/jsp/upload/20160109/56231452305352887.jpeg"/><span class="pic_tag" style="margin: 8px 0px 18px; line-height: 24px; display: block; color: rgb(153, 153, 153); font-size: 12px; width: 600px; padding-left: 0px;"><a href="http://www.mafengwo.cn/poi/18513.html" class="_j_anchor" data-poiid="18513" target="_blank" data-cs-p="ginfo_img_poi_new" style="color: rgb(153, 153, 153); cursor: pointer; outline: 0px; display: inline-block; position: relative;"><em class="house" style="width: 24px; height: 24px; margin-right: 10px; display: inline-block; vertical-align: -6px; background: url(http://css.mafengwo.net/images/post/new_notes/nn_v2/nn_sprite_v17.png) 0px -203px no-repeat;"></em>腾冲火山地热国家地质公园</a></span></div><div id="_j_photo_86862640" data-pid="86862640" class="add_pic _j_anchorcnt" style="margin: 0px 0px 18px; padding: 0px; word-break: break-all; word-wrap: break-word; position: relative; color: rgb(102, 102, 102); font-family: Arial, &#39;Lucida Grande&#39;, &#39;Microsoft Yahei&#39;, &#39;Hiragino Sans GB&#39;, &#39;Hiragino Sans GB W3&#39;, SimSun, STHeiti; font-size: 14px; line-height: 25px; white-space: normal; width: 680px;"><img src="http://localhost:8080/travel/ueditor/jsp/upload/20160109/14211452305357803.jpeg" _src="http://localhost:8080/travel/ueditor/jsp/upload/20160109/14211452305357803.jpeg"/><span class="pic_tag" style="margin: 8px 0px 18px; line-height: 24px; display: block; color: rgb(153, 153, 153); font-size: 12px; width: 600px; padding-left: 0px;"><a href="http://www.mafengwo.cn/poi/2769.html" class="_j_anchor" data-poiid="2769" target="_blank" data-cs-p="ginfo_img_poi_new" style="color: rgb(153, 153, 153); cursor: pointer; outline: 0px; display: inline-block; position: relative;"><em class="house" style="width: 24px; height: 24px; margin-right: 10px; display: inline-block; vertical-align: -6px; background: url(http://css.mafengwo.net/images/post/new_notes/nn_v2/nn_sprite_v17.png) 0px -203px no-repeat;"></em>和顺古镇</a></span></div><div id="_j_photo_86862644" data-pid="86862644" class="add_pic _j_anchorcnt" style="margin: 0px 0px 18px; padding: 0px; word-break: break-all; word-wrap: break-word; position: relative; color: rgb(102, 102, 102); font-family: Arial, &#39;Lucida Grande&#39;, &#39;Microsoft Yahei&#39;, &#39;Hiragino Sans GB&#39;, &#39;Hiragino Sans GB W3&#39;, SimSun, STHeiti; font-size: 14px; line-height: 25px; white-space: normal; width: 680px;"><img src="http://localhost:8080/travel/ueditor/jsp/upload/20160109/10191452305362450.jpeg" _src="http://localhost:8080/travel/ueditor/jsp/upload/20160109/10191452305362450.jpeg"/><span class="pic_tag" style="margin: 8px 0px 18px; line-height: 24px; display: block; color: rgb(153, 153, 153); font-size: 12px; width: 600px; padding-left: 0px;"><a href="http://www.mafengwo.cn/poi/36092.html" class="_j_anchor" data-poiid="36092" target="_blank" data-cs-p="ginfo_img_poi_new" style="color: rgb(153, 153, 153); cursor: pointer; outline: 0px; display: inline-block; position: relative;"><em class="house" style="width: 24px; height: 24px; margin-right: 10px; display: inline-block; vertical-align: -6px; background: url(http://css.mafengwo.net/images/post/new_notes/nn_v2/nn_sprite_v17.png) 0px -203px no-repeat;"></em>瑞丽江</a></span></div><div id="_j_photo_86862649" data-pid="86862649" class="add_pic _j_anchorcnt" style="margin: 0px 0px 18px; padding: 0px; word-break: break-all; word-wrap: break-word; position: relative; color: rgb(102, 102, 102); font-family: Arial, &#39;Lucida Grande&#39;, &#39;Microsoft Yahei&#39;, &#39;Hiragino Sans GB&#39;, &#39;Hiragino Sans GB W3&#39;, SimSun, STHeiti; font-size: 14px; line-height: 25px; white-space: normal; width: 680px;"><img src="http://localhost:8080/travel/ueditor/jsp/upload/20160109/40671452305367281.jpeg" _src="http://localhost:8080/travel/ueditor/jsp/upload/20160109/40671452305367281.jpeg"/><span class="pic_tag" style="margin: 8px 0px 18px; line-height: 24px; display: block; color: rgb(153, 153, 153); font-size: 12px; width: 600px; padding-left: 0px;"><a href="http://www.mafengwo.cn/poi/47934.html" class="_j_anchor" data-poiid="47934" target="_blank" data-cs-p="ginfo_img_poi_new" style="color: rgb(153, 153, 153); cursor: pointer; outline: 0px; display: inline-block; position: relative;"><em class="house" style="width: 24px; height: 24px; margin-right: 10px; display: inline-block; vertical-align: -6px; background: url(http://css.mafengwo.net/images/post/new_notes/nn_v2/nn_sprite_v17.png) 0px -203px no-repeat;"></em>挖色镇</a></span></div><div id="_j_paragraph_2" data-index="2" class="article_title _j_anchorcnt" style="margin: 20px 0px; padding: 0px; word-break: break-all; word-wrap: break-word; position: relative; color: rgb(102, 102, 102); font-family: Arial, &#39;Lucida Grande&#39;, &#39;Microsoft Yahei&#39;, &#39;Hiragino Sans GB&#39;, &#39;Hiragino Sans GB W3&#39;, SimSun, STHeiti; font-size: 14px; line-height: 25px; white-space: normal;"><h2 class="t1" style="margin-right: 0px; margin-bottom: 0px; margin-left: 0px; padding: 0px; font-size: 22px; height: 86px; overflow: hidden; color: rgb(68, 68, 68); background: url(http://css.mafengwo.net/images/post/new_notes/nn_v2/t1.gif) 0px 0px no-repeat;"><span class="_j_anchor" style="line-height: 95px; display: block;">路线篇</span></h2></div><p class="_j_note_content" style="margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; line-height: 25px; font-size: 14px; color: rgb(102, 102, 102); font-family: Arial, &#39;Lucida Grande&#39;, &#39;Microsoft Yahei&#39;, &#39;Hiragino Sans GB&#39;, &#39;Hiragino Sans GB W3&#39;, SimSun, STHeiti; white-space: normal;">两年前，在<a href="http://www.mafengwo.cn/travel-scenic-spot/mafengwo/12711.html" class="link _j_keyword_mdd" data-kw="云南" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">云南</a>完成了一次十七天的蜜月旅行，那时候曾写下这样的文字：<br/><br/>“……<a href="http://www.mafengwo.cn/travel-scenic-spot/mafengwo/12711.html" class="link _j_keyword_mdd" data-kw="云南" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">云南</a>有很多山，很多水，那些大山大水的背后，都有蜂窝们无怨无悔的脚印，我们这次也踏上了自己的足迹。像<a href="http://www.mafengwo.cn/travel-scenic-spot/mafengwo/12700.html" class="link _j_keyword_mdd" data-kw="西藏" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">西藏</a>一样，玩遍<a href="http://www.mafengwo.cn/travel-scenic-spot/mafengwo/12711.html" class="link _j_keyword_mdd" data-kw="云南" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">云南</a>对初次去的童鞋都是天方夜谭，<a href="http://www.mafengwo.cn/travel-scenic-spot/mafengwo/63538.html" class="link _j_keyword_mdd" data-kw="比如" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">比如</a>花开遍野的<a href="http://www.mafengwo.cn/travel-scenic-spot/mafengwo/10482.html" class="link _j_keyword_mdd" data-kw="香格里拉" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">香格里拉</a>、<a href="http://www.mafengwo.cn/travel-scenic-spot/mafengwo/63538.html" class="link _j_keyword_mdd" data-kw="比如" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">比如</a>银杏飘飞的<a href="http://www.mafengwo.cn/travel-scenic-spot/mafengwo/10651.html" class="link _j_keyword_mdd" data-kw="腾冲" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">腾冲</a>、<a href="http://www.mafengwo.cn/travel-scenic-spot/mafengwo/63538.html" class="link _j_keyword_mdd" data-kw="比如" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">比如</a>热带雨林<a href="http://www.mafengwo.cn/travel-scenic-spot/mafengwo/10809.html" class="link _j_keyword_mdd" data-kw="西双版纳" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">西双版纳</a>、或者边陲小镇<a href="http://www.mafengwo.cn/travel-scenic-spot/mafengwo/15812.html" class="link _j_keyword_mdd" data-kw="瑞丽" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">瑞丽</a>。希望这篇游记是美好回忆的开始，也是下次出发的铺垫，希望能给一些人带来欢乐，给自己留下些纪念。”<br/><br/>一转眼三年过去了，去过不少山山水水，忽然很怀念<a href="http://www.mafengwo.cn/travel-scenic-spot/mafengwo/12711.html" class="link _j_keyword_mdd" data-kw="云南" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">云南</a>。正好大师也有一样的想法，我们一拍即合。于是开始整理攻略，制作行程。秋天，<a href="http://www.mafengwo.cn/travel-scenic-spot/mafengwo/10651.html" class="link _j_keyword_mdd" data-kw="腾冲" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">腾冲</a>的<a href="http://www.mafengwo.cn/poi/2781.html" class="link _j_keyword_poi" data-poi_id="2781" data-cs-p="ginfo_txt_poi" data-kw="银杏村" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">银杏村</a>肯定是不能错过的，还有<a href="http://www.mafengwo.cn/travel-scenic-spot/mafengwo/15812.html" class="link _j_keyword_mdd" data-kw="瑞丽" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">瑞丽</a>，那个“只是听名字就很想去”的地方。刚开始只确定了这两个地方，随着浏览蜂蜂们的游记，又陆陆续续确定了<a href="http://www.mafengwo.cn/poi/2775.html" class="link _j_keyword_poi" data-poi_id="2775" data-cs-p="ginfo_txt_poi" data-kw="北海湿地" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">北海湿地</a>、<a href="http://www.mafengwo.cn/poi/17410.html" class="link _j_keyword_poi" data-poi_id="17410" data-cs-p="ginfo_txt_poi" data-kw="诺邓古村" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">诺邓古村</a>等等非主流景点。最后，路程上总会经过的<a href="http://www.mafengwo.cn/travel-scenic-spot/mafengwo/10487.html" class="link _j_keyword_mdd" data-kw="大理" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">大理</a>，也有了再次探访的理由。不过这次去<a href="http://www.mafengwo.cn/travel-scenic-spot/mafengwo/10487.html" class="link _j_keyword_mdd" data-kw="大理" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">大理</a>，就想弥补一下上次<a href="http://www.mafengwo.cn/travel-scenic-spot/mafengwo/10487.html" class="link _j_keyword_mdd" data-kw="大理" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">大理</a>之行的空白点，<a href="http://www.mafengwo.cn/travel-scenic-spot/mafengwo/63538.html" class="link _j_keyword_mdd" data-kw="比如" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">比如</a>夜晚的<a href="http://www.mafengwo.cn/poi/7315824.html" class="link _j_keyword_poi" data-poi_id="7315824" data-cs-p="ginfo_txt_poi" data-kw="人民路" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">人民路</a>酒吧，<a href="http://www.mafengwo.cn/travel-scenic-spot/mafengwo/63538.html" class="link _j_keyword_mdd" data-kw="比如" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">比如</a>夕阳下的<a href="http://www.mafengwo.cn/poi/47934.html" class="link _j_keyword_poi" data-poi_id="47934" data-cs-p="ginfo_txt_poi" data-kw="挖色" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">挖色</a>码头。<br/><br/>最后，我们形成了下面的路线。</p><div id="_j_photo_87146951" data-pid="87146951" class="add_pic _j_anchorcnt" style="margin: 0px 0px 18px; padding: 0px; word-break: break-all; word-wrap: break-word; position: relative; color: rgb(102, 102, 102); font-family: Arial, &#39;Lucida Grande&#39;, &#39;Microsoft Yahei&#39;, &#39;Hiragino Sans GB&#39;, &#39;Hiragino Sans GB W3&#39;, SimSun, STHeiti; font-size: 14px; line-height: 25px; white-space: normal; width: 680px;"><img src="http://localhost:8080/travel/ueditor/jsp/upload/20160109/6861452305372345.jpeg" _src="http://localhost:8080/travel/ueditor/jsp/upload/20160109/6861452305372345.jpeg"/></div><p class="_j_note_content" style="margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; line-height: 25px; font-size: 14px; color: rgb(102, 102, 102); font-family: Arial, &#39;Lucida Grande&#39;, &#39;Microsoft Yahei&#39;, &#39;Hiragino Sans GB&#39;, &#39;Hiragino Sans GB W3&#39;, SimSun, STHeiti; white-space: normal;"><a href="http://www.mafengwo.cn/travel-scenic-spot/mafengwo/12711.html" class="link _j_keyword_mdd" data-kw="云南" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">云南</a>很大，可惜每条路线一般都要从<a href="http://www.mafengwo.cn/travel-scenic-spot/mafengwo/10807.html" class="link _j_keyword_mdd" data-kw="昆明" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">昆明</a>出发，最后再汇集到<a href="http://www.mafengwo.cn/travel-scenic-spot/mafengwo/10807.html" class="link _j_keyword_mdd" data-kw="昆明" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">昆明</a>。<br/><br/>所以<a href="http://www.mafengwo.cn/travel-scenic-spot/mafengwo/12711.html" class="link _j_keyword_mdd" data-kw="云南" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">云南</a>一般有三条经典路线：<br/><br/>西南线：<a href="http://www.mafengwo.cn/travel-scenic-spot/mafengwo/10487.html" class="link _j_keyword_mdd" data-kw="大理" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">大理</a>、<a href="http://www.mafengwo.cn/travel-scenic-spot/mafengwo/10651.html" class="link _j_keyword_mdd" data-kw="腾冲" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">腾冲</a>、<a href="http://www.mafengwo.cn/travel-scenic-spot/mafengwo/30426.html" class="link _j_keyword_mdd" data-kw="芒市" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">芒市</a>、<a href="http://www.mafengwo.cn/travel-scenic-spot/mafengwo/15812.html" class="link _j_keyword_mdd" data-kw="瑞丽" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">瑞丽</a><br/><a href="http://www.mafengwo.cn/travel-scenic-spot/mafengwo/31808.html" class="link _j_keyword_mdd" data-kw="西北" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">西北</a>线：<a href="http://www.mafengwo.cn/travel-scenic-spot/mafengwo/10186.html" class="link _j_keyword_mdd" data-kw="丽江" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">丽江</a>、<a href="http://www.mafengwo.cn/travel-scenic-spot/mafengwo/10482.html" class="link _j_keyword_mdd" data-kw="香格里拉" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">香格里拉</a><br/>&nbsp;&nbsp;&nbsp;南线：<a href="http://www.mafengwo.cn/travel-scenic-spot/mafengwo/11871.html" class="link _j_keyword_mdd" data-kw="红河" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">红河</a>、<a href="http://www.mafengwo.cn/travel-scenic-spot/mafengwo/21358.html" class="link _j_keyword_mdd" data-kw="普洱" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">普洱</a>、<a href="http://www.mafengwo.cn/travel-scenic-spot/mafengwo/10809.html" class="link _j_keyword_mdd" data-kw="西双版纳" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">西双版纳</a><br/><br/>除了在这三条线上不断延伸和扩展。往北还有<a href="http://www.mafengwo.cn/travel-scenic-spot/mafengwo/10121.html" class="link _j_keyword_mdd" data-kw="泸沽湖" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">泸沽湖</a>，往东南还有<a href="http://www.mafengwo.cn/travel-scenic-spot/mafengwo/10810.html" class="link _j_keyword_mdd" data-kw="普者黑" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">普者黑</a>，等等。这次我们旅行的路线是滇西南方向，希望以后还有机会，慢慢把<a href="http://www.mafengwo.cn/travel-scenic-spot/mafengwo/12711.html" class="link _j_keyword_mdd" data-kw="云南" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">云南</a>走全<img src="http://file.mafengwo.net/images/i/face/daily/11@2x.png" style="width: 23px;"/></p><div id="_j_photo_87275706" data-pid="87275706" class="add_pic _j_anchorcnt" style="margin: 0px 0px 18px; padding: 0px; word-break: break-all; word-wrap: break-word; position: relative; color: rgb(102, 102, 102); font-family: Arial, &#39;Lucida Grande&#39;, &#39;Microsoft Yahei&#39;, &#39;Hiragino Sans GB&#39;, &#39;Hiragino Sans GB W3&#39;, SimSun, STHeiti; font-size: 14px; line-height: 25px; white-space: normal; width: 680px;"><img src="http://localhost:8080/travel/ueditor/jsp/upload/20160109/27431452305377286.jpeg" _src="http://localhost:8080/travel/ueditor/jsp/upload/20160109/27431452305377286.jpeg"/></div><div id="_j_paragraph_3" data-index="3" class="article_title _j_anchorcnt" style="margin: 20px 0px; padding: 0px; word-break: break-all; word-wrap: break-word; position: relative; color: rgb(102, 102, 102); font-family: Arial, &#39;Lucida Grande&#39;, &#39;Microsoft Yahei&#39;, &#39;Hiragino Sans GB&#39;, &#39;Hiragino Sans GB W3&#39;, SimSun, STHeiti; font-size: 14px; line-height: 25px; white-space: normal;"><h2 class="t1" style="margin-right: 0px; margin-bottom: 0px; margin-left: 0px; padding: 0px; font-size: 22px; height: 86px; overflow: hidden; color: rgb(68, 68, 68); background: url(http://css.mafengwo.net/images/post/new_notes/nn_v2/t1.gif) 0px 0px no-repeat;"><span class="_j_anchor" style="line-height: 95px; display: block;">愿望篇</span></h2></div><p class="_j_note_content" style="margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; line-height: 25px; font-size: 14px; color: rgb(102, 102, 102); font-family: Arial, &#39;Lucida Grande&#39;, &#39;Microsoft Yahei&#39;, &#39;Hiragino Sans GB&#39;, &#39;Hiragino Sans GB W3&#39;, SimSun, STHeiti; white-space: normal;">确定了地点之后，大师受到了某篇蜂首的启发，也让我写下十件最想完成的事情，让我在这次旅途中一一实现。于是接下来有一个月的时间，我都在补充和修改这十件事情，最终形成了这样一个清单：<br/><br/>1、坐热气球去看火<a href="http://www.mafengwo.cn/travel-scenic-spot/mafengwo/65820.html" class="link _j_keyword_mdd" data-kw="山口" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">山口</a><br/>2、在最美<a href="http://www.mafengwo.cn/poi/2781.html" class="link _j_keyword_poi" data-poi_id="2781" data-cs-p="ginfo_txt_poi" data-kw="银杏村" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">银杏村</a>看一次日出<br/>3、在<a href="http://www.mafengwo.cn/travel-scenic-spot/mafengwo/59482.html" class="link _j_keyword_mdd" data-kw="热海" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">热海</a>吃一串煮鸡蛋<br/>4、泡一次天然温泉<br/>5、吃白果炖鸡<br/>6、在<a href="http://www.mafengwo.cn/poi/2769.html" class="link _j_keyword_poi" data-poi_id="2769" data-cs-p="ginfo_txt_poi" data-kw="和顺古镇" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">和顺古镇</a>喝茶<br/>7、和星空合影<br/>8、去国门发微博，点我在这里<br/>9、去<a href="http://www.mafengwo.cn/poi/36092.html" class="link _j_keyword_poi" data-poi_id="36092" data-cs-p="ginfo_txt_poi" data-kw="瑞丽江" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">瑞丽江</a>看日落<br/>10、去湿地踩草<br/><br/>今天，等我再次翻看这次旅行的照片，回忆我完成的这十件事的时候，忽然领会了村上春树说的”小确幸“，这真是一种稍纵即逝的幸福呵。</p><div id="_j_paragraph_4" data-index="4" class="article_title _j_anchorcnt" style="margin: 20px 0px; padding: 0px; word-break: break-all; word-wrap: break-word; position: relative; color: rgb(102, 102, 102); font-family: Arial, &#39;Lucida Grande&#39;, &#39;Microsoft Yahei&#39;, &#39;Hiragino Sans GB&#39;, &#39;Hiragino Sans GB W3&#39;, SimSun, STHeiti; font-size: 14px; line-height: 25px; white-space: normal;"><h2 class="t1" style="margin-right: 0px; margin-bottom: 0px; margin-left: 0px; padding: 0px; font-size: 22px; height: 86px; overflow: hidden; color: rgb(68, 68, 68); background: url(http://css.mafengwo.net/images/post/new_notes/nn_v2/t1.gif) 0px 0px no-repeat;"><span class="_j_anchor" style="line-height: 95px; display: block;">摄影篇</span></h2></div><p class="_j_note_content" style="margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; line-height: 25px; font-size: 14px; color: rgb(102, 102, 102); font-family: Arial, &#39;Lucida Grande&#39;, &#39;Microsoft Yahei&#39;, &#39;Hiragino Sans GB&#39;, &#39;Hiragino Sans GB W3&#39;, SimSun, STHeiti; white-space: normal;">相机：佳能5D3&nbsp;+&nbsp;索尼&nbsp;A7s<br/>镜头：适马20/1.4，腾龙70-300VC，美能达58/1.2<br/>后期：Lightroom&nbsp;+&nbsp;RAW格式<br/>拼图：Picasa<br/><br/>Tips：<br/><img src="http://file.mafengwo.net/images/i/face/daily/30@2x.png" style="width: 23px;"/>&nbsp;风景照一般是20/1.4和70-300VC照的，其中星空基本都是20/1.4，人像一般用58/1.2。<br/><img src="http://file.mafengwo.net/images/i/face/daily/30@2x.png" style="width: 23px;"/>&nbsp;后期的时候一般增加对比度和饱和度，但是最重要的是要有个好天气和好模特<img src="http://file.mafengwo.net/images/i/face/daily/8@2x.png" style="width: 23px;"/><br/><img src="http://file.mafengwo.net/images/i/face/daily/30@2x.png" style="width: 23px;"/>&nbsp;几个重要的摄影点：11月的<a href="http://www.mafengwo.cn/poi/2781.html" class="link _j_keyword_poi" data-poi_id="2781" data-cs-p="ginfo_txt_poi" data-kw="银杏村" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">银杏村</a>、夕阳下的<a href="http://www.mafengwo.cn/poi/2775.html" class="link _j_keyword_poi" data-poi_id="2775" data-cs-p="ginfo_txt_poi" data-kw="北海湿地" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">北海湿地</a>、姐告大桥下的<a href="http://www.mafengwo.cn/poi/36092.html" class="link _j_keyword_poi" data-poi_id="36092" data-cs-p="ginfo_txt_poi" data-kw="瑞丽江" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">瑞丽江</a>、日落的<a href="http://www.mafengwo.cn/poi/47934.html" class="link _j_keyword_poi" data-poi_id="47934" data-cs-p="ginfo_txt_poi" data-kw="挖色" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">挖色</a>码头</p><div id="_j_paragraph_5" data-index="5" class="article_title _j_anchorcnt" style="margin: 20px 0px; padding: 0px; word-break: break-all; word-wrap: break-word; position: relative; color: rgb(102, 102, 102); font-family: Arial, &#39;Lucida Grande&#39;, &#39;Microsoft Yahei&#39;, &#39;Hiragino Sans GB&#39;, &#39;Hiragino Sans GB W3&#39;, SimSun, STHeiti; font-size: 14px; line-height: 25px; white-space: normal;"><h2 class="t1" style="margin-right: 0px; margin-bottom: 0px; margin-left: 0px; padding: 0px; font-size: 22px; height: 86px; overflow: hidden; color: rgb(68, 68, 68); background: url(http://css.mafengwo.net/images/post/new_notes/nn_v2/t1.gif) 0px 0px no-repeat;"><span class="_j_anchor" style="line-height: 95px; display: block;">交通篇</span></h2></div><p class="_j_note_content" style="margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; line-height: 25px; font-size: 14px; color: rgb(102, 102, 102); font-family: Arial, &#39;Lucida Grande&#39;, &#39;Microsoft Yahei&#39;, &#39;Hiragino Sans GB&#39;, &#39;Hiragino Sans GB W3&#39;, SimSun, STHeiti; white-space: normal;"><a href="http://www.mafengwo.cn/travel-scenic-spot/mafengwo/10065.html" class="link _j_keyword_mdd" data-kw="北京" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">北京</a>到<a href="http://www.mafengwo.cn/travel-scenic-spot/mafengwo/10651.html" class="link _j_keyword_mdd" data-kw="腾冲" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">腾冲</a>没有直飞，只能去<a href="http://www.mafengwo.cn/travel-scenic-spot/mafengwo/10807.html" class="link _j_keyword_mdd" data-kw="昆明" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">昆明</a>转机，所以我们20号下班就赶着飞到长水机场，第二天上午再飞<a href="http://www.mafengwo.cn/travel-scenic-spot/mafengwo/10651.html" class="link _j_keyword_mdd" data-kw="腾冲" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">腾冲</a>。&nbsp;这次去<a href="http://www.mafengwo.cn/travel-scenic-spot/mafengwo/12711.html" class="link _j_keyword_mdd" data-kw="云南" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">云南</a>也是要去好几个地方，坐大巴车比较辗转，所以我们选择了包车游，从<a href="http://www.mafengwo.cn/travel-scenic-spot/mafengwo/10651.html" class="link _j_keyword_mdd" data-kw="腾冲" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">腾冲</a>开始到<a href="http://www.mafengwo.cn/travel-scenic-spot/mafengwo/10487.html" class="link _j_keyword_mdd" data-kw="大理" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">大理</a>结束。<a href="http://www.mafengwo.cn/travel-scenic-spot/mafengwo/12711.html" class="link _j_keyword_mdd" data-kw="云南" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">云南</a>很多的包车都只在当地，很难得才找到李师傅。李师傅人特别好，为人忠厚老实，脾气也好，熟悉当地各种情况，给我们讲解了很多的当地特色，这一路非常感谢李师傅的照顾。<br/><br/>他从<a href="http://www.mafengwo.cn/travel-scenic-spot/mafengwo/10651.html" class="link _j_keyword_mdd" data-kw="腾冲" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">腾冲</a>机场接到我们，玩遍<a href="http://www.mafengwo.cn/travel-scenic-spot/mafengwo/10651.html" class="link _j_keyword_mdd" data-kw="腾冲" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">腾冲</a>后，向西南一路到了<a href="http://www.mafengwo.cn/travel-scenic-spot/mafengwo/15812.html" class="link _j_keyword_mdd" data-kw="瑞丽" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">瑞丽</a>，然后再回头一路向<a href="http://www.mafengwo.cn/travel-scenic-spot/mafengwo/16215.html" class="link _j_keyword_mdd" data-kw="东北" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">东北</a>，走到了<a href="http://www.mafengwo.cn/travel-scenic-spot/mafengwo/17823.html" class="link _j_keyword_mdd" data-kw="诺邓" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">诺邓</a>，最后我们在<a href="http://www.mafengwo.cn/travel-scenic-spot/mafengwo/10487.html" class="link _j_keyword_mdd" data-kw="大理" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">大理</a>分别。这一路上，除了<a href="http://www.mafengwo.cn/travel-scenic-spot/mafengwo/10651.html" class="link _j_keyword_mdd" data-kw="腾冲" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">腾冲</a>，师傅一路的餐饮住宿都和我们一起，但是这个季节算是<a href="http://www.mafengwo.cn/travel-scenic-spot/mafengwo/12711.html" class="link _j_keyword_mdd" data-kw="云南" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">云南</a>旅游的淡季，所以因此的花销也不算太多。<br/><br/>为避免广告嫌疑，这里就不写李师傅的电话了，感兴趣的小伙伴可以站内短信或者留言，强烈推荐哦<img src="http://file.mafengwo.net/images/i/face/daily/12@2x.png" style="width: 23px;"/></p><div id="_j_paragraph_6" data-index="6" class="article_title _j_anchorcnt" style="margin: 20px 0px; padding: 0px; word-break: break-all; word-wrap: break-word; position: relative; color: rgb(102, 102, 102); font-family: Arial, &#39;Lucida Grande&#39;, &#39;Microsoft Yahei&#39;, &#39;Hiragino Sans GB&#39;, &#39;Hiragino Sans GB W3&#39;, SimSun, STHeiti; font-size: 14px; line-height: 25px; white-space: normal;"><h2 class="t1" style="margin-right: 0px; margin-bottom: 0px; margin-left: 0px; padding: 0px; font-size: 22px; height: 86px; overflow: hidden; color: rgb(68, 68, 68); background: url(http://css.mafengwo.net/images/post/new_notes/nn_v2/t1.gif) 0px 0px no-repeat;"><span class="_j_anchor" style="line-height: 95px; display: block;">游记篇</span></h2></div><div id="_j_paragraph_7" data-index="7" class="article_title _j_anchorcnt" style="margin: 20px 0px; padding: 0px; word-break: break-all; word-wrap: break-word; position: relative; color: rgb(102, 102, 102); font-family: Arial, &#39;Lucida Grande&#39;, &#39;Microsoft Yahei&#39;, &#39;Hiragino Sans GB&#39;, &#39;Hiragino Sans GB W3&#39;, SimSun, STHeiti; font-size: 14px; line-height: 25px; white-space: normal;"><h2 class="t3" style="margin-right: 0px; margin-bottom: 0px; margin-left: 0px; padding: 0px; font-size: 22px; height: 86px; overflow: hidden; color: rgb(68, 68, 68); background: url(http://css.mafengwo.net/images/post/new_notes/nn_v2/t3.gif) 0px 0px no-repeat;"><span class="_j_anchor" style="line-height: 102px; display: block; margin-left: 72px;">Day1-2：腾冲 - 银杏村</span></h2></div><p class="_j_note_content" style="margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; line-height: 25px; font-size: 14px; color: rgb(102, 102, 102); font-family: Arial, &#39;Lucida Grande&#39;, &#39;Microsoft Yahei&#39;, &#39;Hiragino Sans GB&#39;, &#39;Hiragino Sans GB W3&#39;, SimSun, STHeiti; white-space: normal;">Tips：<br/><img src="http://file.mafengwo.net/images/i/face/daily/30@2x.png" style="width: 23px;"/>&nbsp;最适合拍银杏的季节在11月中旬前后<br/><img src="http://file.mafengwo.net/images/i/face/daily/30@2x.png" style="width: 23px;"/>&nbsp;现在<a href="http://www.mafengwo.cn/poi/2781.html" class="link _j_keyword_poi" data-poi_id="2781" data-cs-p="ginfo_txt_poi" data-kw="银杏村" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">银杏村</a>开始收门票了，40一个人，但是每天早8点前晚20点后可以逃票<br/><img src="http://file.mafengwo.net/images/i/face/daily/30@2x.png" style="width: 23px;"/>&nbsp;这里的住宿基本不用提前预订，农家乐非常多，村子很小，住在哪里都是黄金位置</p><div id="_j_photo_87090573" data-pid="87090573" class="add_pic _j_anchorcnt" style="margin: 0px 0px 18px; padding: 0px; word-break: break-all; word-wrap: break-word; position: relative; color: rgb(102, 102, 102); font-family: Arial, &#39;Lucida Grande&#39;, &#39;Microsoft Yahei&#39;, &#39;Hiragino Sans GB&#39;, &#39;Hiragino Sans GB W3&#39;, SimSun, STHeiti; font-size: 14px; line-height: 25px; white-space: normal; width: 680px;"><br/></div><p class="_j_note_content" style="margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; line-height: 25px; font-size: 14px; color: rgb(102, 102, 102); font-family: Arial, &#39;Lucida Grande&#39;, &#39;Microsoft Yahei&#39;, &#39;Hiragino Sans GB&#39;, &#39;Hiragino Sans GB W3&#39;, SimSun, STHeiti; white-space: normal;">秋天是彩色的，秋天是丰收的，秋天是我们最喜欢的季节。银杏更是秋色里必不可少的。前两年就经常看见关于<a href="http://www.mafengwo.cn/poi/2781.html" class="link _j_keyword_poi" data-poi_id="2781" data-cs-p="ginfo_txt_poi" data-kw="银杏村" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">银杏村</a>的照片，大师老说要看银杏就要去这里。在我的印象里<a href="http://www.mafengwo.cn/poi/2781.html" class="link _j_keyword_poi" data-poi_id="2781" data-cs-p="ginfo_txt_poi" data-kw="银杏村" target="_blank" style="color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(148, 142, 142);">银杏村</a>是宁静的，祥和的，成熟的银杏挂满枝头，金黄的银杏叶随风飘洒，铺满村道小巷，整个村庄变成一片金色的海洋，让我们向往不已...<br/><br/>我们迫不及待，第一站就要沉浸在金色的世界里。</p><div id="_j_photo_86664928" data-pid="86664928" class="add_pic _j_anchorcnt" style="margin: 0px 0px 18px; padding: 0px; word-break: break-all; word-wrap: break-word; position: relative; color: rgb(102, 102, 102); font-family: Arial, &#39;Lucida Grande&#39;, &#39;Microsoft Yahei&#39;, &#39;Hiragino Sans GB&#39;, &#39;Hiragino Sans GB W3&#39;, SimSun, STHeiti; font-size: 14px; line-height: 25px; white-space: normal; width: 680px;"><br/><span class="pic_tag" style="margin: 8px 0px 18px; line-height: 24px; display: block; color: rgb(153, 153, 153); font-size: 12px; width: 600px; padding-left: 0px;"><a href="http://www.mafengwo.cn/poi/2781.html" class="_j_anchor" data-poiid="2781" target="_blank" data-cs-p="ginfo_img_poi_new" style="color: rgb(153, 153, 153); cursor: pointer; outline: 0px; display: inline-block; position: relative;"><em class="house" style="width: 24px; height: 24px; margin-right: 10px; display: inline-block; vertical-align: -6px; background: url(http://css.mafengwo.net/images/post/new_notes/nn_v2/nn_sprite_v17.png) 0px -203px no-repeat;"></em>固东镇银杏村</a></span></div><p class="_j_note_content" style="margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; line-height: 25px; font-size: 14px; color: rgb(102, 102, 102); font-family: Arial, &#39;Lucida Grande&#39;, &#39;Microsoft Yahei&#39;, &#39;Hiragino Sans GB&#39;, &#39;Hiragino Sans GB W3&#39;, SimSun, STHeiti; white-space: normal;">我们住的双乐农家乐<img src="http://file.mafengwo.net/images/i/face/daily/1@2x.png" style="width: 23px;"/>&nbsp;门口已经铺满了银杏叶，我就踩在这金色的地毯上</p><div id="_j_photo_86688881" data-pid="86688881" class="add_pic _j_anchorcnt" style="margin: 0px 0px 18px; padding: 0px; word-break: break-all; word-wrap: break-word; position: relative; color: rgb(102, 102, 102); font-family: Arial, &#39;Lucida Grande&#39;, &#39;Microsoft Yahei&#39;, &#39;Hiragino Sans GB&#39;, &#39;Hiragino Sans GB W3&#39;, SimSun, STHeiti; font-size: 14px; line-height: 25px; white-space: normal; width: 680px;"><img src="http://localhost:8080/travel/ueditor/jsp/upload/20160109/98381452305389805.jpeg" _src="http://localhost:8080/travel/ueditor/jsp/upload/20160109/98381452305389805.jpeg"/><span class="pic_tag" style="margin: 8px 0px 18px; line-height: 24px; display: block; color: rgb(153, 153, 153); font-size: 12px; width: 600px; padding-left: 0px;"><a href="http://www.mafengwo.cn/poi/2781.html" class="_j_anchor" data-poiid="2781" target="_blank" data-cs-p="ginfo_img_poi_new" style="color: rgb(153, 153, 153); cursor: pointer; outline: 0px; display: inline-block; position: relative;"><em class="house" style="width: 24px; height: 24px; margin-right: 10px; display: inline-block; vertical-align: -6px; background: url(http://css.mafengwo.net/images/post/new_notes/nn_v2/nn_sprite_v17.png) 0px -203px no-repeat;"></em>固东镇银杏村</a></span></div><p class="_j_note_content" style="margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; line-height: 25px; font-size: 14px; color: rgb(102, 102, 102); font-family: Arial, &#39;Lucida Grande&#39;, &#39;Microsoft Yahei&#39;, &#39;Hiragino Sans GB&#39;, &#39;Hiragino Sans GB W3&#39;, SimSun, STHeiti; white-space: normal;">家家户户都挂满了玉米，真是个丰收的季节</p><div id="_j_photo_86664925" data-pid="86664925" class="add_pic _j_anchorcnt" style="margin: 0px 0px 18px; padding: 0px; word-break: break-all; word-wrap: break-word; position: relative; color: rgb(102, 102, 102); font-family: Arial, &#39;Lucida Grande&#39;, &#39;Microsoft Yahei&#39;, &#39;Hiragino Sans GB&#39;, &#39;Hiragino Sans GB W3&#39;, SimSun, STHeiti; font-size: 14px; line-height: 25px; white-space: normal; width: 680px;"><img src="http://localhost:8080/travel/ueditor/jsp/upload/20160109/54851452305402730.jpeg" _src="http://localhost:8080/travel/ueditor/jsp/upload/20160109/54851452305402730.jpeg"/><span class="pic_tag" style="margin: 8px 0px 18px; line-height: 24px; display: block; color: rgb(153, 153, 153); font-size: 12px; width: 600px; padding-left: 0px;"><a href="http://www.mafengwo.cn/poi/2781.html" class="_j_anchor" data-poiid="2781" target="_blank" data-cs-p="ginfo_img_poi_new" style="color: rgb(153, 153, 153); cursor: pointer; outline: 0px; display: inline-block; position: relative;"><em class="house" style="width: 24px; height: 24px; margin-right: 10px; display: inline-block; vertical-align: -6px; background: url(http://css.mafengwo.net/images/post/new_notes/nn_v2/nn_sprite_v17.png) 0px -203px no-repeat;"></em>固东镇银杏村</a></span></div><p class="_j_note_content" style="margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; line-height: 25px; font-size: 14px; color: rgb(102, 102, 102); font-family: Arial, &#39;Lucida Grande&#39;, &#39;Microsoft Yahei&#39;, &#39;Hiragino Sans GB&#39;, &#39;Hiragino Sans GB W3&#39;, SimSun, STHeiti; white-space: normal;"><img src="http://localhost:8080/travel/ueditor/jsp/upload/20160109/83071452305448870.jpeg" _src="http://localhost:8080/travel/ueditor/jsp/upload/20160109/83071452305448870.jpeg"/></p><p><br/></p>
