CREATE TABLE indexBigImage-- 首页大图
(
	ibiid CHAR(32) PRIMARY KEY, -- 编号
	image TEXT,-- 保存位置
	`order` INT UNIQUE AUTO_INCREMENT, -- 序号，如每次只读取序号最大的4张图片（自增的属性必须是主键或者unique）
	cityid CHAR(32),-- 所指向的城市的id号
	FOREIGN KEY (cityid) REFERENCES city(cityid) -- 外键
);

CREATE TABLE findPartner -- 寻找同行驴友
(
	fpaid INT PRIMARY KEY AUTO_INCREMENT,-- id
	title VARCHAR(100), -- 标题
	simage VARCHAR(200), -- 封面的小图片
	startPlace VARCHAR(100), -- 出发地
	endPlace VARCHAR(100), -- 目的地	
	startTime VARCHAR(30), -- 出发时间
	days INT, -- 持续时间（天数）
	signinNumber INT, -- 已申请人数（现在做起来有点小难）
	sex BOOLEAN, -- 性别是否为男性(0False为女，1True为男)
	phoneNumber CHAR(32),-- 联系方式（手机号、微信、qq都可以）
	introduce MEDIUMTEXT, -- 保存成html代码的用户编写的同行介绍的内容
	userid CHAR(32), -- 外键引用的userid
	FOREIGN KEY (userid) REFERENCES	`user`(userid)
);
