CREATE TABLE attraction-- 景点
(
	attid CHAR(32) PRIMARY KEY,-- 景点id
	`name` VARCHAR(100) , -- 景点名
	image VARCHAR(300),-- 图片
	`position` VARCHAR(200),-- 景点位置
	introduce VARCHAR(500), -- 介绍
	cityid CHAR(32) ,-- 所属城市
	del BOOLEAN DEFAULT 0,-- 是否被删除
	FOREIGN KEY (cityid) REFERENCES city(cityid)-- 外键声明
);
