CREATE TABLE travelAgency( -- 旅行社表，旅行社可以发布跟团游项目
	tagid INT PRIMARY KEY, -- id
	username CHAR(32), -- 用户名，用于登录
	`password` CHAR(32), -- 密码
	`name` CHAR(64), -- 旅行社的名字
	`desc` VARCHAR(500) -- 旅行社的简介
);
