CREATE TABLE travelItem(-- 团游项目
	titid INT PRIMARY KEY AUTO_INCREMENT,
	simage VARCHAR(200), -- 封面小图片
	title VARCHAR(100), -- 标题
	price INT,-- 价格
	startTime VARCHAR(40),-- 开始时间
	days INT,-- 持续天数
	startPlace VARCHAR(40),-- 出发地点
	endPlace VARCHAR(40),-- 目标地点
	content MEDIUMTEXT, -- 保存成html代码的项目介绍的内容
	tagid INT ,-- 旅行社id， 外键，引用travelAgency旅行社表的主键
	FOREIGN KEY (tagid) REFERENCES travelAgency(tagid)
);
