-- 以上数据库为要用到的，下面的数据库为暂时不用的--
CREATE TABLE applyForPartner -- 申请同行
(
	afpid CHAR(32) PRIMARY KEY ,-- 申请同行id
        phoneNumber CHAR(32) ,-- 联系方式
	number INT,--同行人数
	sex BOOLEAN ,-- 性别
	remark VAECHAR(500), -- 备注
	
	userid CHAR(32) ,-- 申请用户的id
	fpaid CHAR(32), -- 同行驴友招募的id
	FOREIGN KEY (userid) REFERENCES	`user`(userid),
	FOREIGN KEY (fpaid) REFERENCES	`findPartner`(fpaid)
	
);


-- select * from attraction
CREATE TABLE attractionStrategy -- 景点攻略
(
	astid CHAR(32) PRIMARY KEY, -- id
	simage VARCHAR(200), -- 小封面图片
	`time` DATETIME ,-- 发表时间
	
	content VARCHAR(200), -- 保存的html的位置
	attid CHAR(32),-- 所属景点的id
	FOREIGN KEY (attid) REFERENCES attraction(attid) -- 外键
);
CREATE TABLE food-- 美食
(
	foodid CHAR(32) PRIMARY KEY,-- 美食id
	`name` VARCHAR(100) , -- 美食名字
	introduce VARCHAR(500), -- 介绍
	cityid CHAR(32) ,-- 所属城市
	FOREIGN KEY (cityid) REFERENCES city(cityid)-- 外键声明
);
CREATE TABLE foodStrategy -- 美食攻略
(
	fstid CHAR(32) PRIMARY KEY, -- id
	simage VARCHAR(200), -- 小封面图片
	`time` DATETIME ,-- 发表时间
	foodid CHAR(32),-- 所属的美食
	FOREIGN KEY (foodid) REFERENCES food(foodid) -- 外键
);
CREATE TABLE `comment`-- 评论（只有评论，没有回复）
(
	comid CHAR(32) PRIMARY KEY,-- 评论id
	userid CHAR(32) ,-- 引用的用户id，是这个用户发的评论
	`type` CHAR(32) ,-- 评论类型，对城市的评论为c，对景点的评论为a，对美食的评论为f
	strid CHAR(32),-- 指向的攻略的id，因为攻略有3种，所以这里不添加外键声明了
	FOREIGN KEY (userid) REFERENCES `user`(userid)
);
CREATE TABLE advice-- 投诉及建议
(
	advid CHAR(32) PRIMARY KEY,-- id
	userid CHAR(32), -- 用户id
	content VARCHAR(1000), -- 投诉或者建议内容
	FOREIGN KEY (userid) REFERENCES `user`(userid)
);

CREATE TABLE admin -- 管理员表，用于管理员登录
(
	admid CHAR(32) PRIMARY KEY,-- id
	username CHAR(32) NOT NULL,-- 登录名称
	`password` CHAR(32) NOT NULL, -- 密码
	`super` BOOLEAN -- 是否为超级管理员，超级管理员可以对普通管理员进行管理，
);

